/*
Let's practice props and mapping components on our todo list app!

I've created a js file with some todos data in it, which I'm imported into this file. (Normally this data would come from an API call, not a local file). 

Challenge: Using the array map method, render a child component for each todo item in the todosData array and pass the relevant data to it.
*/


import React from 'react'
import data from './Data.json'
import TodoItem from './TodoItem'


export class App extends React.Component{

    constructor(){
        super()
        this.state = {TODOS: data.todos}
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(id) {
        this.setState(prevState => {
            const updatedTodos = prevState.TODOS.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed
                }
                return todo
            })
            return {
                TODOS: updatedTodos
            }
        })
    }

    // handleChange(id){
    //     console.log("HANS")
    //     this.setState((prevState) => {
    //         const newList = prevState.TODOS.map((singleTodo) => {
    //             if(singleTodo.id === id){
    //                 singleTodo.completed = !singleTodo.completed
    //             }
    //             return singleTodo
    //         } )
    //     return {
    //         TODOS: newList
    //     }
        
    //     })
    // }
    render(){

         const todoItem = this.state.TODOS.map( item => (<TodoItem key={item.id} todo={item} handleChange={this.handleChange}/>))


        
        return(
        <div className="todo-list">    
        {todoItem}
        </div>)
    }
}
export default App
