        super(props)
        this.state = {
            show : false,
            adventsData:[]        }
        this.handleDate = this.handleDate.bind(this)
        this.componentDidMount = this.componentDidMount(this)
        this.hideModal = this.hideModal(this)
        this.showModal = this.showModal(this)
        
    }
    
    componentDidMount(){
        
        DataFromREST.getData().then(response => {
            
