export class Greeting extends React.Component{

    render(){
        const date = new Date()
        const hours = date.getHours()
        let timeOfDay
        
        if (hours < 12) {
            timeOfDay = "morning"
        } else if (hours >= 12 && hours < 17) {
            timeOfDay = "afternoon"
        } else {
            timeOfDay = "night"
        }

        return (
            <h1>Good {timeOfDay} to you, sir or madam!</h1>
        )
    }
}


ReactDOM.render(<App />, document.getElementById("root"))
