import React from "react"
import TodoItem from "./TodoItem"
import todosData from "./Data.js"


class App extends React.Component {
    constructor() {
        super()
        this.handleChange = this.handleChange.bind(this)
        this.state = {
            todoInState: todosData
        }
        
    }
    
    handleChange(id) {
        console.log("Das ist die ID: " +id)
        this.setState(prevState => {
            const updatedTodos = prevState.todoInState.map(todo => {
                console.log(todo.id)
                if (todo.id === id) {
                   console.log(`Das sind die beiden Ids: ${todo.id} ${id}`)
                   console.log(todo.completed)
                    todo.completed = !todo.completed
                    console.log(todo.completed)

                }
                return todo
            })
            return {
                todoInState: updatedTodos
            }
        })
    }

   
    
    render() {
        console.log("Wir sind im RENDER")
        const todoItems = this.state.todoInState.map(item => <TodoItem key={item.id} item={item} handleChange={this.handleChange}/>)
        
        return (
            <div className="todo-list">
                {todoItems}
            </div>
        )    
    }
}

export default App
