import React from 'react'
import ReactDOM from 'react-dom'
import Door from './Door'
import AdventData from './DoorData.json'
import DetailDoor from './DetailDoor'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            adventsData: AdventData.doors
        }
        this.handleDate = this.handleDate.bind(this)
    }
    
    componentDidMount() { (2)
		client({method: 'GET', path: '/api/employees'}).done(response => {
			this.setState({employees: response.entity._embedded.employees});
		});
    }
    
    handleDate(date){
        var localDate = new Date()
        //localDate  = localDate.getMonth( ) + 1 +'/'+ localDate.getDate( ) + '/' +localDate.getFullYear( );
        localDate = "12/10/2020"

        
        if(localDate == date || date <= localDate){
            console.log("Ja diese törchen funktioniert.")
            alert("Yeah this door can be opened")

            this.state.adventsData.map(singleDoor => {if(date == singleDoor.date){
                alert(`Typ des Törchen =  ${singleDoor.typ} `)
            }}

            )
            
        }else{
            console.log("Diese Tür kannst du nicht öffnen")
            alert("This date is in the Future, Pleas wait for it")
        }
    }
    render() {
        const todoItems = this.state.adventsData.map(item => <Door key={item.id} item={item} handleClick={this.handleDate}/>)
        
        return (
            <div className="todo-list">
                {todoItems}
            </div>
        )    
    }
}

export default App
