import React from 'react'
import ReactDOM from 'react-dom'
import Door from './Door'
import AdventData from './DoorData.json'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            adventsData: AdventData.doors
        }
    }
    
    render() {
        const todoItems = this.state.adventsData.map(item => <Door key={item.id} item={item} onClick={this.handleDate(item.date)}/>)
        
        return (
            <div className="todo-list">
                {todoItems}
            </div>
        )    
    }
}

export default App
