import axios from 'axios'

const USER_DATA_LINK = 'http://localhost:8080/advent/test'

class DataFromREST {

    getData(){
        return axios.get(USER_DATA_LINK);

    }

    postData(){

        axios.post(USER_DATA_LINK)
    }

}

export default new DataFromREST();
