import React from 'react'



function Modal (props : any) {
    console.log(props.dataTransfered)

    const showHideClassName = props.show ? "modal display-block" : "modal display-none";

    var message : any = "DefaultMessage"
    
    if(props.dataTransfered !== undefined && props.dataTransfered !== null){
      var dataId = props.dataTransfered.id;
      var date;
      var dataType;
     if(dataId === -1){
       message = "Die Tür kann noch nicht geöffnet werden."
       var date = props.dataTransfered.date;
       dataType = "Failed"

     } else{
       message = props.dataTransfered.type
       dataType = props.dataTransfered.type
     }
    
    
    console.log(dataType)
    
      
    }
    
    return (

      <div className={showHideClassName}>
          <div className="containerModal">
                 
          
        <section className="modal-main">
          
          
    <h1 className="hanspeter">{message}</h1> 
    <div className="contentBox"><iframe className="ytVideo" width="1280" height="720" src="https://www.youtube.com/embed/E8gmARGvPlI?autoplay=1" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" ></iframe></div>
          <button className ="closeButton"onClick={props.handleClose}>close</button>  
              
        </section>
   
        </div> 
      </div>
    
    )
  }

   export default Modal
